import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { CirclePicker } from 'react-color';
import './Home.css';

import api from '../../services/api';

const Home = () => {
	const [color, setColor] = useState('lightgrey');
	const [colors, setColors] = useState([]);
	useEffect(() => {
		api.get('colors').then((response) => {
			console.log(response);
			setColors(response.data);
		});
	}, []);

	const handleCreateSnake = async (data) => {
		try {
			await api.post('points', data);
		} finally {
			alert('error');
		}
	};

	const generatePosition = () => {
		const newPosition = [
			[
				Math.floor((Math.random() * mapSize[0]) / mapScale),
				Math.floor((Math.random() * mapSize[1]) / mapScale),
			],
		];
		return newPosition
	}

	return (
		<div className="main">
			<h1>Welcome to Snake Battle Royale!</h1>
			<p>Pick your color:</p>
			<CirclePicker
				colors={colors}
				color={color}
				onChange={(pickColor) => setColor(pickColor.hex)}
			/>
			<button
				className={color === 'lightgrey' ? 'inactive' : 'active'}
				style={{ backgroundColor: color }}
				onClick={
					color === 'lightgrey' ? () => handleCreateSnake({color: color, position: generatePosition, points:0}) : () => null
				}
			>
				<Link
					className={color === 'lightgrey' ? 'inactiveLink' : 'activeLink'}
					to={color === 'lightgrey' ? '/' : 'in-game'}
				>
					<i
						style={{ color: 'white' }}
						className="fa fa-check fa-2x"
						aria-hidden="true"
					></i>
				</Link>
			</button>
		</div>
	);
};

export default Home;
