import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { CirclePicker } from 'react-color';
import './Home.css';

import api from '../../services/api';

const Home = () => {
	const [color, setColor] = useState('lightgrey');
	const [colors, setColors] = useState([]);
	const [map, setMap] = useState(null);
	useEffect(() => {
		api.get('colors').then((response) => {
			setColors(response.data);
		});
	}, []);

	useEffect(() => {
		api.get('map').then((response) => {
			setMap(response.data);
		});
	}, []);

	const handleCreateSnake = (data) => {
		api.post('snakes', data).then((response) => console.log(response));
	};

	const generatePosition = () => [
		[
			Math.floor((Math.random() * map.size[0]) / map.scale),
			Math.floor((Math.random() * map.size[1]) / map.scale),
		],
	];

	const position = generatePosition();

	return (
		<div className="main">
			<h1>Welcome to Snake Battle Royale!</h1>
			<p>Pick your color:</p>
			<CirclePicker
				colors={colors}
				color={color}
				onChange={(pickColor) => setColor(pickColor.hex)}
			/>
			<button
				className={color === 'lightgrey' ? 'inactive' : 'active'}
				style={{ backgroundColor: color }}
				onClick={
					color === 'lightgrey'
						? () =>
								handleCreateSnake({
									color: color,
									position: generatePosition(),
									points: 0,
								})
						: () => null
				}
			>
				<Link
					className={color === 'lightgrey' ? 'inactiveLink' : 'activeLink'}
					to={color === 'lightgrey' ? '/' : 'in-game'}
				>
					<i
						style={{ color: 'white' }}
						className="fa fa-check fa-2x"
						aria-hidden="true"
					></i>
				</Link>
			</button>
		</div>
	);
};

export default Home;
