import React, { useState, useRef, useEffect } from 'react';
import { useInterval } from './useInterval.js';
import {
	mapSize,
	snakeSpawnPos,
	appleSpawnPos,
	mapScale,
	speed,
	compass,
} from './initializer.js';
import './App.css';

const App = () => {
	const canvasRef = useRef();
	const [snake, setSnake] = useState(snakeSpawnPos);
	const [apple, setApple] = useState(appleSpawnPos);
	const [momentum, setMomentum] = useState([0, -1]);
	const [snakeSpeed, setSnakeSpeed] = useState(null);
	const [gameOver, setGameOver] = useState(false);

	useEffect(() => {
		const context = canvasRef.current.getContext('2d');
		context.setTransform(mapScale, 0, 0, mapScale, 0, 0);
	});

	const startGame = () => {
		setSnake(snakeSpawnPos);
		setApple(appleSpawnPos);
		setMomentum([0, -1]);
		setSnakeSpeed(speed);
		setGameOver(false);
	};

	const move = (keyCode) => setMomentum(compass[keyCode]);

	const endGame = () => {
		setSnakeSpeed(null);
		setGameOver(true);
	};

	const spawnApple = () => {};

	const checkCollision = (head, snek = snake) => {
		if (
			head[0] * mapScale >= mapSize[0] ||
			head[0] < 0 ||
			head[1] * mapScale >= mapSize[1] ||
			head[1] < 0
		) return true;
	};

	const gameOn = () => {
		const snakeCopy = JSON.parse(JSON.stringify(snake));
		const newSnakeHead = [
			snakeCopy[0][0] + momentum[0],
			snakeCopy[0][1] + momentum[1],
		];
		snakeCopy.unshift(newSnakeHead);
		if (checkCollision(newSnakeHead)) endGame();
		snakeCopy.pop();
		setSnake(snakeCopy);
	};

	useEffect(() => {
		const context = canvasRef.current.getContext('2d');
		context.clearRect(0, 0, mapSize[0], mapSize[1]);
		context.fillStyle = 'lightgreen';
		snake.forEach(([x, y]) => context.fillRect(x, y, 1, 1));
		context.fillStyle = 'lightblue';
		context.fillRect(apple[0], apple[1], 1, 1);
	}, [snake, apple, gameOver]);

	useInterval(() => gameOn(), snakeSpeed);

	return (
		<div role="button" tabIndex="-1" onKeyDown={(event) => move(event.keyCode)}>
			<input />
			<canvas
				style={{ border: '1px solid black' }}
				ref={canvasRef}
				width={`${mapSize[0]}px`}
				height={`${mapSize[1]}px`}
			/>
			{gameOver && <div>GAME OVER!</div>}
			<button onClick={startGame}>Start Game</button>
		</div>
	);
};

export default App;
