import React, { useState, useRef, useEffect } from 'react';
import { useInterval } from './useInterval.js';
import {
	mapSize,
	snakeSpawnPos,
	appleSpawnPos,
	mapScale,
	snakeSpeed,
	compass,
} from './initializer.js';
import './App.css';

const App = () => {
	const canvasRef = useRef();
  const [snake, setSnake] = useState(snakeSpawnPos);
  const [apple, setApple] = useState(appleSpawnPos);
  const [momentum, setMomentum] = useState([0, -1]);
  const [speed, setSpeed] = useState(null);
  const [gameOver, setGameOver] = useState(false);

  useEffect(() => {
    const context = canvasRef.current.getContext('2d');
    context.setTransform(mapScale, 0, 0, mapScale, 0, 0);
  })

	const startGame = () => {};

	const move = ({key}) => (key in compass) && setMomentum(compass[key]);

	const endGame = () => {};

	const spawnApple = () => {};

	const checkCollision = () => {};

	const gameOn = () => {
    const snakeCopy = JSON.parse(JSON.stringify(snake));
    const newSnakeHead = [snakeCopy[0][0] + momentum[0]]
  };

	useEffect(() => {
    const context = canvasRef.current.getContext('2d');
    context.clearRect(0, 0, mapSize[0], mapSize[1])
    context.fillStyle = 'lightgreen';
    snake.forEach(([x, y]) => context.fillRect(x, y, 1, 1));
    context.fillStyle = 'lightblue';
    context.fillRect(apple[0], apple[1], 1, 1)
  }, [snake, apple, gameOver]);

	return (
		<div role="button" tabIndex="0" onKeyDown={(event) => move(event)}>
			<canvas
				style={{ border: '1px solid black' }}
				ref={canvasRef}
				width={`${mapSize[0]}px`}
				height={`${mapSize[1]}px`}
			/>
			{gameOver && <div>GAME OVER!</div>}
			<button onClick={startGame}>Start Game</button>
		</div>
	);
};

export default App;
